// Cards
/*-----------------------------------------------------------
TABLE OF CONTENTS:
-------------------------------------------------------------
1.0 Close Card Btn
2.0 Hidden Card Btn
3.0 Refresh Card Btn
------------------------------------------------------------*/
$(document).ready(function(){
"use strict";

var card_close_btn = $(".btn__card--close"),
	card_hidden_btn = $(".btn__card--hidden"),
	card_hidden_icon = $(".btn__card--hidden i.fa"),
	card_refresh_btn = $(".btn__card--refresh"),
	card = $(".card"),
	card_refresh = $("<div class='card--refresh'><div class='spinner--3 spinner--primary spinner--md'></div </div>");

/*-----------------------------------------------------------
1.0 Close Card Btn
------------------------------------------------------------*/
card_close_btn.on("click", function(e){
	e.preventDefault();
	$(this).closest(".card").parent().hide();
});

/*-----------------------------------------------------------
2.0 Hidden Card Btn
------------------------------------------------------------*/
card_hidden_btn.bind("click", function(e){
	e.preventDefault();
	$(this).closest(".card__heading").next().slideToggle(300);
});

/*-----------------------------------------------------------
3.0 Hidden Card Btn
------------------------------------------------------------*/
card_refresh_btn.on("click", function(e){
	e.preventDefault();
	$(this).closest(".card__heading").next().append(card_refresh);
	setTimeout(function() {
		card_refresh.remove();
	}, 2850);	
});

}); //end