$(function(){
	$.fn.editable.defaults.mode = 'inline';
	//defaults
	$.fn.editable.defaults.url = '../php/edit.php'; 

	//enable / disable
	$('#enable').click(function() {
	   $('#user .editable').editable('toggleDisabled');
	});    

	//editables 
	$('#username').editable({
		type: 'text',
		pk: 1,
		name: 'username',
		title: 'Enter username'
	});

	$('#firstname').editable({
		validate: function(value) {
		   if($.trim(value) == '') return 'This field is required';
		}
	});

	$('#sex').editable({
		prepend: "not selected",
		source: [
			{value: 1, text: 'Male'},
			{value: 2, text: 'Female'}
		],
		display: function(value, sourceData) {
			 var colors = {"": "gray", 1: "green", 2: "blue"},
				 elem = $.grep(sourceData, function(o){return o.value == value;});
				 
			 if(elem.length) {    
				 $(this).text(elem[0].text).css("color", colors[value]); 
			 } else {
				 $(this).empty(); 
			 }
		}   
	});    

	$('#group').editable({
	   	showbuttons: false,
	   	prepend: "Admin",
		source: [
			{value: 1, text: 'Admin'},
			{value: 2, text: 'Services'},
			{value: 3, text: 'Customer'},
			{value: 4, text: 'Support'},
			{value: 5, text: 'Quest'}
		]
	});   

	$('#vacation').editable({
		datepicker: {
			todayBtn: 'linked'
		} 
	});  
		
	$('#dob').editable();
		  
	$('#event').editable({
		placement: 'right',
		combodate: {
			firstItem: 'name'
		}
	});          

	$('#comments').editable({
		showbuttons: 'bottom'
	});   

	$('#fruits').editable({
	   pk: 1,
	   limit: 3,
	   source: [
		{value: 1, text: 'banana'},
		{value: 2, text: 'peach'},
		{value: 3, text: 'apple'},
		{value: 4, text: 'watermelon'},
		{value: 5, text: 'orange'}
	   ]
	});             
	
});